<style>
	/* Algemene SVG container styles */
	.round-lines,
	.world-shape,
	.triangle-shape {
		overflow: visible;
	}

	/* ===== ROUND VERTICAL LINES ANIMATIES ===== */
	.round-lines svg {
		/* Geen rotatie meer, alleen paths animeren */
	}

	.round-lines path {
		animation: subtlePathPulse 3s ease-in-out infinite;
	}

	/* Individuele delays voor golvend effect */
	.round-lines .path-1 {
		animation-delay: 0s;
	}
	.round-lines .path-2 {
		animation-delay: -0.3s;
	}
	.round-lines .path-3 {
		animation-delay: -0.6s;
	}
	.round-lines .path-4 {
		animation-delay: -0.9s;
	}
	.round-lines .path-5 {
		animation-delay: -1.2s;
	}
	.round-lines .path-6 {
		animation-delay: -1.5s;
	}
	.round-lines .path-7 {
		animation-delay: -1.8s;
	}
	.round-lines .path-8 {
		animation-delay: -2.1s;
	}
	.round-lines .path-9 {
		animation-delay: -2.4s;
	}
	.round-lines .path-10 {
		animation-delay: -2.7s;
	}
	.round-lines .path-11 {
		animation-delay: -3s;
	}
	.round-lines .path-12 {
		animation-delay: -3.3s;
	}
	.round-lines .path-13 {
		animation-delay: -3.6s;
	}
	.round-lines .path-14 {
		animation-delay: -3.9s;
	}
	.round-lines .path-15 {
		animation-delay: -4.2s;
	}

	/* ===== WORLD SHAPE ANIMATIES ===== */
	.world-shape svg {
		animation: worldShapeFloat 12s ease-in-out infinite;
	}

	.world-shape .world-path-1 {
		animation: morphPath 10s ease-in-out infinite;
		transform-origin: center;
	}

	.world-shape .world-path-2 {
		animation: morphPath 10s ease-in-out infinite reverse;
		animation-delay: -5s;
		transform-origin: center;
	}

	/* Blue world shape - light traveling through paths */
	.world-shape.blue path:first-of-type {
		animation: lightTravelPath 3s ease-in-out infinite;
	}

	.world-shape.blue path:last-of-type {
		animation: lightTravelPath 3s ease-in-out infinite;
		animation-delay: 1.5s;
	}

	/* ===== TRIANGLE SHAPE ANIMATIES ===== */
	.triangle-shape svg {
		/* Geen oscillatie meer, alleen paths animeren */
	}

	.triangle-shape .triangle-path-1 {
		animation: subtleTrianglePulse 4s ease-in-out infinite;
		transform-origin: center;
	}

	.triangle-shape .triangle-path-2 {
		animation: subtleTrianglePulse 4s ease-in-out infinite;
		animation-delay: -2s;
		transform-origin: center;
	}

	/* ===== KEYFRAMES ===== */

	/* Subtiele pulse animatie voor round lines paths */
	@keyframes subtlePathPulse {
		0%,
		100% {
			opacity: 0.5;
			stroke-width: 0.4;
		}
		50% {
			opacity: 0.9;
			stroke-width: 0.7;
		}
	}

	/* Subtiele pulse animatie voor triangle paths */
	@keyframes subtleTrianglePulse {
		0%,
		100% {
			opacity: 0.4;
			fill-opacity: 0.4;
		}
		50% {
			opacity: 0.8;
			fill-opacity: 0.8;
		}
	}

	/* Float beweging voor world shape */
	@keyframes worldShapeFloat {
		0%,
		100% {
			transform: translateY(0px) rotate(0deg);
		}
		33% {
			transform: translateY(-8px) rotate(2deg);
		}
		66% {
			transform: translateY(8px) rotate(-2deg);
		}
	}

	/* Morph effect voor organische vormen */
	@keyframes morphPath {
		0%,
		100% {
			transform: scale(1) skew(0deg);
			opacity: 0.8;
		}
		25% {
			transform: scale(1.05) skew(1deg);
			opacity: 0.9;
		}
		50% {
			transform: scale(0.98) skew(-1deg);
			opacity: 1;
		}
		75% {
			transform: scale(1.02) skew(0.5deg);
			opacity: 0.9;
		}
	}

	/* Zoom effect voor triangle paths */
	@keyframes triangleZoom {
		0%,
		100% {
			transform: scale(1) rotate(0deg);
			opacity: 0.6;
		}
		50% {
			transform: scale(1.08) rotate(180deg);
			opacity: 1;
		}
	}

	/* Light traveling through paths effect voor blue world shape */
	@keyframes lightTravelPath {
		0%,
		100% {
			opacity: 0.3;
			filter: drop-shadow(0 0 0px rgba(14, 79, 99, 0));
		}
		50% {
			opacity: 1;
			filter: drop-shadow(0 0 8px rgba(14, 79, 99, 0.8));
		}
	}

	/* ===== HOVER EFFECTS ===== */
	/* Geen hover effecten meer - animaties zijn standaard al mooi */

	/* ===== RESPONSIVE ===== */
	@media (prefers-reduced-motion: reduce) {
		.round-lines path,
		.world-shape svg,
		.world-shape .world-path-1,
		.world-shape .world-path-2,
		.triangle-shape .triangle-path-1,
		.triangle-shape .triangle-path-2 {
			animation: none;
		}
	}

	/* Voor kleinere schermen, behoud dezelfde snelheid */
	@media (max-width: 768px) {
		.round-lines path {
			animation-duration: 3s;
		}
		.world-shape svg {
			animation-duration: 12s;
		}
		.triangle-shape .triangle-path-1,
		.triangle-shape .triangle-path-2 {
			animation-duration: 4s;
		}
	}
</style>
